$Name= $env:computername;Get-ADComputer -Filter {Name -like $Name} | ForEach-Object {$computer= $_;Get-ADPrincipalGroupMembership -Identity $_|Select-Object @{Name = 'Computer Name'; Expression ={$computer.SamAccountName}},@{Name = 'GroupName'; Expression ={$_.SamAccountName}},@{Name = 'Distinguished Name'; Expression ={$_.distinguishedname}}, @{Name = 'Member'; Expression = {$computer.DNSHostName}}} | Select -Property GroupName -ExpandProperty GroupName
